Scripts.OnEnter:
if ( not self.isDisabled ) then
    GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT");
    GameTooltip:SetText("Use different color for player's interested in loot from a boss", nil, nil, nil, nil, true);
end

if ( not self.isDisabled ) then
    GameTooltip:SetOwner(self, "ANCHOR_BOTTOMRIGHT");
    GameTooltip:SetText("Use different color for player's interested in an item", nil, nil, nil, nil, true);
end
